import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import com.eviware.soapui.model.testsuite.TestRunner.Status

def testProject = testRunner.testCase.testSuite.project
def testSuite = testProject.testSuites["Device ID API"]
def testCase = testSuite.testCases["TestSetup"]


def builder = new groovy.json.JsonBuilder()
def modifyCustomerId = context.expand( '${#Project#ModifyCustomerId}' )
def knownMachineId = context.expand( '${#TestSuite#KnownMachineId}' )
def knownFriendlyName = context.expand( '${#TestSuite#KnownFriendlyName}' )


builder.DeviceDto(
	ClientVersion: '',
	CurrentClientVersion: '',
	CurrentDeploymentVersion: '',
	CustomerId: "'"+modifyCustomerId+"'",
	DeploymentVersion: '',
	FriendlyName: "'"+knownFriendlyName+"'",
	MachineId: "'"+knownMachineId+"'",
	ReportNumberSeed: '',
	ServerId: '',
	Status: '',
	//I expect to see THIS and Guriqbals comment on lines 1 and 2
	)
log.info builder.toString()
testSuite.setPropertyValue("DeviceDto", builder.toString())

con:testSuite.con:setupScript:
def utils = new com.eviware.soapui.support.GroovyUtilsPro( context )
context.dbConnection = utils.getJdbcConnection( "Devices" )

context.statement = context.dbConnection.createStatement()

def modifyCustomerId = context.expand( '${#Project#ModifyCustomerId}' )
def rs = context.statement.executeUpdate("UPDATE PersistentDevices SET ClientVersion = '', DeploymentVersion = 0 WHERE CustomerId = '"+modifyCustomerId+"'")

if(rs != 0)
	log.info "Updated"
else
	log.info "Error"


context.statement.close()

